sect1.example:
  Ссылки и объекты


<?php

class A
{
    public $foo = 1;
}

$a = new A();
$b = $a;     // Переменные $a и $b — копии одного и того же идентификатора
             // ($a) = ($b) = <id>
$b->foo = 2;
echo $a->foo."\n";


$c = new A();
$d = &$c;    // Переменные $c и $d — ссылки
             // ($c, $d) = <id>

$d->foo = 2;
echo $c->foo."\n";


$e = new A();

function foo($obj)
{
    // ($obj) = ($e) = <id>
    $obj->foo = 2;
}

foo($e);
echo $e->foo."\n";

?>


  &example.outputs;


2
2
2
