para.example:
   Пример побитовых операции И, ИЛИ и исключающее ИЛИ (AND, OR и XOR) над целыми числами


<?php

/*
 * Не обращайте внимания на верхний раздел кода,
 * это просто форматирование для более ясного вывода
 */
$format = '(%1$2d = %1$04b) = (%2$2d = %2$04b)'
    . ' %3$s (%4$2d = %4$04b)' . "\n"
;

echo <<<EOH
 ----------  ----------- -- ----------
 Результат   Значение    Тестовая операция
 ----------  ----------- -- ----------
EOH;


/*
 * Вот сами примеры.
 */

$values = array(0, 1, 2, 4, 8);
$test = 1 + 4;

echo "\n Побитовое И (AND) \n";
foreach ($values as $value) {
    $result = $value & $test;
    printf($format, $result, $value, '&', $test);
}

echo "\n Побитовое (включающее) ИЛИ (OR) \n";
foreach ($values as $value) {
    $result = $value | $test;
    printf($format, $result, $value, '|', $test);
}

echo "\n Побитовое исключающее ИЛИ (XOR) \n";
foreach ($values as $value) {
    $result = $value ^ $test;
    printf($format, $result, $value, '^', $test);
}

?>


   &example.outputs;


 ---------     ---------  -- ---------
 Результат     Значение   Тестовая операция
 ---------     ---------  -- ---------
 Побитовое И
( 0 = 0000) = ( 0 = 0000) & ( 5 = 0101)
( 1 = 0001) = ( 1 = 0001) & ( 5 = 0101)
( 0 = 0000) = ( 2 = 0010) & ( 5 = 0101)
( 4 = 0100) = ( 4 = 0100) & ( 5 = 0101)
( 0 = 0000) = ( 8 = 1000) & ( 5 = 0101)

 Побитовое ИЛИ
( 5 = 0101) = ( 0 = 0000) | ( 5 = 0101)
( 5 = 0101) = ( 1 = 0001) | ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) | ( 5 = 0101)
( 5 = 0101) = ( 4 = 0100) | ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) | ( 5 = 0101)

 Побитовое исключающее ИЛИ (XOR)
( 5 = 0101) = ( 0 = 0000) ^ ( 5 = 0101)
( 4 = 0100) = ( 1 = 0001) ^ ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) ^ ( 5 = 0101)
( 1 = 0001) = ( 4 = 0100) ^ ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) ^ ( 5 = 0101)

Пример сдвига битов в целых числах
<?php
/* * Несколько примеров */
echo "\n--- СДВИГ ВПРАВО НАД ПОЛОЖИТЕЛЬНЫМИ ЦЕЛЫМИ (НАТУРАЛЬНЫМИ) ЧИСЛАМИ ---\n";
$val = 4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'слева вставилась копия знакового бита');
$val = 4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places);
$val = 4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'биты сдвинулись за правый край');
$val = 4; $places = 4; $res = $val >> $places; p($res, $val, '>>', $places, 'то же, что и выше; нельзя сдвинуть дальше 0');
echo "\n--- СДВИГ ВПРАВО НАД ОТРИЦАТЕЛЬНЫМИ ЦЕЛЫМИ ЧИСЛАМИ ---\n";
$val = -4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'слева вставилась копия знакового бита');
$val = -4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places, 'биты сдвинулись за правый край');
$val = -4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'то же, что и выше; нельзя сдвинуть дальше -1');
echo "\n--- СДВИГ ВЛЕВО НАД ПОЛОЖИТЕЛЬНЫМИ ЦЕЛЫМИ (НАТУРАЛЬНЫМИ) ЧИСЛАМИ ---\n";
$val = 4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'правый край дополнился нулями');
$val = 4; $places = (PHP_INT_SIZE * 8) - 4; $res = $val << $places; p($res, $val, '<<', $places);
$val = 4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places, 'знаковые биты сдвинулись');
$val = 4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'биты сдвинулись за левый край');
echo "\n--- СДВИГ ВЛЕВО НАД ОТРИЦАТЕЛЬНЫМИ ЦЕЛЫМИ ЧИСЛАМИ ---\n";
$val = -4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'правый дополнился нулями');
$val = -4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places);
$val = -4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'биты сдвинулись за левый край, включая знаковый бит');
/* * Не обращайте внимания на этот нижний раздел кода, * это просто форматирование для более ясного вывода */
function p($res, $val, $op, $places, $note = '') { $format = '%0' . (PHP_INT_SIZE * 8) . "b\n";
printf("Выражение: %d = %d %s %d\n", $res, $val, $op, $places);
echo " Десятичный вид:\n"; printf(" val=%d\n", $val); printf(" res=%d\n", $res);
echo " Двоичный вид:\n"; printf(' val=' . $format, $val); printf(' res=' . $format, $res);
if ($note) { echo " ЗАМЕЧАНИЕ: $note\n"; }
echo "\n"; }
?>
&example.outputs.32bit;
--- СДВИГ ВПРАВО НАД ПОЛОЖИТЕЛЬНЫМИ ЦЕЛЫМИ (НАТУРАЛЬНЫМИ) ЧИСЛАМИ --- Выражение: 2 = 4 >> 1 Десятичный вид: val=4 res=2 Двоичный вид: val=00000000000000000000000000000100 res=00000000000000000000000000000010 ЗАМЕЧАНИЕ: слева вставилась копия знакового бита
Выражение: 1 = 4 >> 2 Десятичный вид: val=4 res=1 Двоичный вид: val=00000000000000000000000000000100 res=00000000000000000000000000000001
Выражение: 0 = 4 >> 3 Десятичный вид: val=4 res=0 Двоичный вид: val=00000000000000000000000000000100 res=00000000000000000000000000000000 ЗАМЕЧАНИЕ: биты сдвинулись за правый край
Выражение: 0 = 4 >> 4 Десятичный вид: val=4 res=0 Двоичный вид: val=00000000000000000000000000000100 res=00000000000000000000000000000000 ЗАМЕЧАНИЕ: то же, что и выше; нельзя сдвинуть дальше 0
--- СДВИГ ВПРАВО НА ОТРИЦАТЕЛЬНЫХ ЦЕЛЫХ ЧИСЛАХ --- Выражение: -2 = -4 >> 1 Десятичный вид: val=-4 res=-2 Двоичный вид: val=11111111111111111111111111111100 res=11111111111111111111111111111110 ЗАМЕЧАНИЕ: слева вставилась копия знакового бита
Выражение: -1 = -4 >> 2 Десятичный вид: val=-4 res=-1 Двоичный вид: val=11111111111111111111111111111100 res=11111111111111111111111111111111 ЗАМЕЧАНИЕ: биты сдвинулись за правый край
Выражение: -1 = -4 >> 3 Десятичный вид: val=-4 res=-1 Двоичный вид: val=11111111111111111111111111111100 res=11111111111111111111111111111111 ЗАМЕЧАНИЕ: то же, что и выше; нельзя сдвинуть дальше -1
--- СДВИГ ВЛЕВО НАД ПОЛОЖИТЕЛЬНЫМИ ЦЕЛЫМИ (НАТУРАЛЬНЫМИ) ЧИСЛАМИ --- Выражение: 8 = 4 << 1 Десятичный вид: val=4 res=8 Двоичный вид: val=00000000000000000000000000000100 res=00000000000000000000000000001000 ЗАМЕЧАНИЕ: правый край дополнился нулями
Выражение: 1073741824 = 4 << 28 Десятичный вид: val=4 res=1073741824 Двоичный вид: val=00000000000000000000000000000100 res=01000000000000000000000000000000
Выражение: -2147483648 = 4 << 29 Десятичный вид: val=4 res=-2147483648 Двоичный вид: val=00000000000000000000000000000100 res=10000000000000000000000000000000 ЗАМЕЧАНИЕ: знаковые биты сдвинулись
Выражение: 0 = 4 << 30 Десятичный вид: val=4 res=0 Двоичный вид: val=00000000000000000000000000000100 res=00000000000000000000000000000000 ЗАМЕЧАНИЕ: биты сдвинулись за левый край
--- СДВИГ ВЛЕВО НАД ОТРИЦАТЕЛЬНЫМИ ЦЕЛЫМИ ЧИСЛАМИ --- Выражение: -8 = -4 << 1 Десятичный вид: val=-4 res=-8 Двоичный вид: val=11111111111111111111111111111100 res=11111111111111111111111111111000 ЗАМЕЧАНИЕ: правый край дополнился нулями
Выражение: -2147483648 = -4 << 29 Десятичный вид: val=-4 res=-2147483648 Двоичный вид: val=11111111111111111111111111111100 res=10000000000000000000000000000000
Выражение: 0 = -4 << 30 Десятичный вид: val=-4 res=0 Двоичный вид: val=11111111111111111111111111111100 res=00000000000000000000000000000000 ЗАМЕЧАНИЕ: биты сдвинулись за левый край, включая знаковый бит
