&reftitle.changelog;





    &Version;
    &Description;





    8.0.0

     Параметр callback теперь принимает значение null.


   &array.changelog.by-ref;

  &reftitle.examples;



    Пример использования функции array_filter


<?php

function odd($var)
{
    // Функция возвращает информацию о том, нечётно ли входное целое число
    return $var & 1;
}

function even($var)
{
    // Функция возвращает информацию о том, чётно ли входное целое число
    return !($var & 1);
}

$array1 = ['a' => 1, 'b' => 2, 'c' => 3, 'd' => 4, 'e' => 5];
$array2 = [6, 7, 8, 9, 10, 11, 12];

echo "Нечётные:\n";
print_r(array_filter($array1, "odd"));

echo "Чётные:\n";
print_r(array_filter($array2, "even"));

?>


    &example.outputs;


Нечётные:
Array
(
    [a] => 1
    [c] => 3
    [e] => 5
)
Чётные:
Array
(
    [0] => 6
    [2] => 8
    [4] => 10
    [6] => 12
)





     Пример вызова функции array_filter
     без аргумента callback



<?php

$entry = [
    0 => 'foo',
    1 => false,
    2 => -1,
    3 => null,
    4 => '',
    5 => '0',
    6 => 0,
];

print_r(array_filter($entry));

?>


    &example.outputs;


Array
(
    [0] => foo
    [2] => -1
)





     Пример вызова функции array_filter
     с флагом mode



<?php

$arr = ['a' => 1, 'b' => 2, 'c' => 3, 'd' => 4];

var_dump(
    array_filter(
        $arr,
        function ($k) {
            return $k == 'b';
        },
        ARRAY_FILTER_USE_KEY
    )
);

var_dump(
    array_filter(
        $arr,
        function ($v, $k) {
            return $k == 'b' || $v == 4;
        },
        ARRAY_FILTER_USE_BOTH
    )
);

?>


    &example.outputs;


array(1) {
  ["b"]=>
  int(2)
}
array(2) {
  ["b"]=>
  int(2)
  ["d"]=>
  int(4)
}
