refentry.refsect1:
&reftitle.description;
mixedkey arrayarray
key возвращает индекс текущего элемента массива.

  &reftitle.examples;



    Пример использования key


<?php
$array = array(
    'fruit1' => 'apple',
    'fruit2' => 'orange',
    'fruit3' => 'grape',
    'fruit4' => 'apple',
    'fruit5' => 'apple');

// этот цикл выведет все ключи ассоциативного массива,
// значения которых равны "apple"
while ($fruit_name = current($array)) {
    if ($fruit_name == 'apple') {
        echo key($array).'<br />';
    }
    next($array);
}
?>


    &example.outputs;


fruit1<br />
fruit4<br />
fruit5<br />
