refentry.refsect1:
&reftitle.description;
stringcubrid_db_name arrayresult intindex
Функция извлекает имя базы данных из результата вызова функции cubrid_list_dbs.

  &reftitle.examples;




     Пример использования функции cubrid_db_name



<?php

error_reporting(E_ALL);

$conn = cubrid_connect('localhost', 33000, 'demodb', 'dba', '');
$db_list = cubrid_list_dbs($conn);

$i = 0;
$cnt = count($db_list);
while ($i < $cnt) {
    echo cubrid_db_name($db_list, $i) . "\n";
    $i++;
}

?>


    &example.outputs;


demodb
