refentry.refsect1:
&reftitle.parameters;


  data



    Параметр получает массив с данными представления объекта DateTime.

  &reftitle.examples;

   Пример обработки данных представления объекта даты и времени методом DateTime::__unserialize


<?php

$serializedDate = 'O:8:"DateTime":3:{s:4:"date";s:26:"2025-03-27 00:00:00.000000";s:13:"timezone_type";i:3;s:8:"timezone";s:3:"UTC";}';
var_dump(unserialize($serializedDate));

?>


   &examples.outputs;


object(DateTime)#1 (3) {
  ["date"]=>
  string(26) "2025-03-27 00:00:00.000000"
  ["timezone_type"]=>
  int(3)
  ["timezone"]=>
  string(3) "UTC"
}
