refentry.refsect1:
&reftitle.description;
resourceeio_fstat mixedfd intpri callablecallback mixeddata
eio_fstat возвращает информацию о состоянии файла в result аргументе callback

&reftitle.parameters;


  fd



    Поток, указатель на сокет, или числовой дескриптор файла.




  pri

   &eio.request.pri.values;



  callback

   &eio.callback.proto;



  data



    Произвольная переменная, передаваемая в callback-функцию.

  &reftitle.examples;

   Пример использования eio_lstat


<?php
// Создание временного файла
$tmp_filename = dirname(__FILE__) ."/eio-file.tmp";
touch($tmp_filename);

/* Вызывается после завершения eio_fstat() */
function my_res_cb($data, $result) {
 // Выводит массив с информацией о состоянии файла
 var_dump($result);

 if ($data['fd']) {
  // Закрывает временный файл
  eio_close($data['fd']);
  eio_event_loop();
 }
 // Удаляет временный файл
 @unlink($data['file']);
}

/* Вызывается после завершения eio_open() */
function my_open_cb($data, $result) {
 // Подготовка данных для callback
 $d = array(
  'fd'  => $result,
  'file'=> $data
 );
 // Получение информации о файле
 eio_fstat($result, EIO_PRI_DEFAULT, "my_res_cb", $d);
 // Выполнение запросов
 eio_event_loop();
}

// Открытие временного файла
eio_open($tmp_filename, EIO_O_RDONLY, NULL, EIO_PRI_DEFAULT,
  "my_open_cb", $tmp_filename);
eio_event_loop();
?>


