refentry.refsect1:
  &reftitle.examples;



    Пример использования fbsql_read_blob


<?php
$link = fbsql_pconnect("localhost", "_SYSTEM", "secret")
    or die("Не удалось подключиться");
$sql = "SELECT BLOB_COLUMN FROM BLOB_TABLE;";
$rs = fbsql_query($sql, $link);
$row_data = fbsql_fetch_row($rs);
// $row_data[0] теперь будет содержать данные BLOB для первой строки
fbsql_free_result($rs);

$rs = fbsql_query($sql, $link);
fbsql_set_lob_mode($rs, FBSQL_LOB_HANDLE);
$row_data = fbsql_fetch_row($rs);
// $row_data[0] теперь будет содержать дескриптор данных BLOB в первой строке
$blob_data = fbsql_read_blob($row_data[0]);
fbsql_free_result($rs);

?>
