refentry.refsect1:
  &reftitle.examples;



    Пример использования fbsql_read_clob


<?php
$link = fbsql_pconnect("localhost", "_SYSTEM", "secret")
    or die("Не удалось подключиться");
$sql = "SELECT CLOB_COLUMN FROM CLOB_TABLE;";
$rs = fbsql_query($sql, $link);
$row_data = fbsql_fetch_row($rs);
// $row_data[0] теперь будет содержать данные CLOB для первой строки
fbsql_free_result($rs);

$rs = fbsql_query($sql, $link);
fbsql_set_lob_mode($rs, FBSQL_LOB_HANDLE);
$row_data = fbsql_fetch_row($rs);
// $row_data[0] теперь будет содержать дескриптор данных CLOB в первой строке
$clob_data = fbsql_read_clob($row_data[0]);
fbsql_free_result($rs);

?>
