refentry.refsect1:
  &reftitle.examples;



    Пример использования gnupg_geterrorinfo в процедурном стиле


<?php
$res = gnupg_init();
// вызывается без ошибок
print_r(gnupg_geterrorinfo($res));
?>


    &example.outputs;


array(4) {
  ["generic_message"]=>
  bool(false)
  ["gpgme_code"]=>
  int(0)
  ["gpgme_source"]=>
  string(18) "Unspecified source"
  ["gpgme_message"]=>
  string(7) "Success"
}




    Пример использования gnupg_geterrorinfo в объектно-ориентированном стиле


<?php
$gpg = new gnupg();
// вызов с ошибкой
$gpg->decrypt('abc');
// должна отобразиться информация об ошибке
print_r($gpg->geterrorinfo());
?>


    &example.outputs;


array(4) {
  ["generic_message"]=>
  string(14) "decrypt failed"
  ["gpgme_code"]=>
  int(117440570)
  ["gpgme_source"]=>
  string(5) "GPGME"
  ["gpgme_message"]=>
  string(7) "No data"
}
