    Примеры использования id3_get_version


<?php
$version = id3_get_version( "path/to/example.mp3" );
if ($version & ID3_V1_0) {
    echo "Содержит 1.x тэг\n";
}
if ($version & ID3_V1_1) {
    echo "Содержит 1.1 тэг\n";
}
if ($version & ID3_V2) {
    echo "Содержит 2.x тэг\n";
}
?>


