refentry.refsect1:
&reftitle.description;
boolimagefilter resourceimage intfiltertype intarg1 intarg2 intarg3 intarg4
imagefilter применяет заданный фильтр filtertype к изображению image.

&reftitle.changelog;







     &Version;
     &Description;





     5.3.0

      Добавлена поддержка пикселирования (IMGFILTER_PIXELATE).




     5.2.5

      Добавлена поддержка прозрачности IMG_FILTER_COLORIZE.

  &reftitle.examples;



    Пример использования imagefilter с фильтром grayscale


<?php
$im = imagecreatefrompng('dave.png');

if($im && imagefilter($im, IMG_FILTER_GRAYSCALE))
{
    echo 'Изображение преобразовано к градациям серого.';

    imagepng($im, 'dave.png');
}
else
{
    echo 'Преобразование не удалось.';
}

imagedestroy($im);
?>








     Пример использования imagefilter с фильтром яркости



<?php
$im = imagecreatefrompng('sean.png');

if($im && imagefilter($im, IMG_FILTER_BRIGHTNESS, 20))
{
    echo 'Яркость изображения изменена.';

    imagepng($im, 'sean.png');
    imagedestroy($im);
}
else
{
    echo 'Изменить яркость не удалось.';
}
?>








     Пример использования imagefilter с фильтром colorize



<?php
$im = imagecreatefrompng('philip.png');

/* R, G, B, so 0, 255, 0 is green */
if($im && imagefilter($im, IMG_FILTER_COLORIZE, 0, 255, 0))
{
    echo 'Изображение преобразовано к оттенкам зеленого.';

    imagepng($im, 'philip.png');
    imagedestroy($im);
}
else
{
    echo 'Преобразование не удалось.';
}
?>








     Пример использования imagefilter с фильтром negate



<?php
// Определим нашу функцию, которую можно использовать в PHP
// без imagefilter()
function negate($im)
{
    if(function_exists('imagefilter'))
    {
        return imagefilter($im, IMG_FILTER_NEGATE);
    }

    for($x = 0; $x < imagesx($im); ++$x)
    {
        for($y = 0; $y < imagesy($im); ++$y)
        {
            $index = imagecolorat($im, $x, $y);
            $rgb = imagecolorsforindex($index);
            $color = imagecolorallocate($im, 255 - $rgb['red'], 255 - $rgb['green'], 255 - $rgb['blue']);

            imagesetpixel($im, $x, $y, $color);
        }
    }

    return(true);
}

$im = imagecreatefromjpeg('kalle.jpg');

if($im && negate($im))
{
    echo 'Изображение инвертировано успешно.';

    imagejpeg($im, 'kalle.jpg', 100);
    imagedestroy($im);
}
else
{
    echo 'Инвертировать изображение не удалось.';
}
?>








     Пример использования imagefilter с фильтром pixelate



<?php
// загрузка PHP логотипа, нам нужно 2 штуки
// для сравнения
$logo1 = imagecreatefrompng('./php.png');
$logo2 = imagecreatefrompng('./php.png');

// подопытный экземпляр
$output = imagecreatetruecolor(imagesx($logo1) * 2, imagesy($logo1));

// Применение пикселирования к каждому изображению с размером блока в 3 пиксела
imagefilter($logo1, IMG_FILTER_PIXELATE, 3);
imagefilter($logo2, IMG_FILTER_PIXELATE, 3, true);

// Совмещение различий в выходном изображении
imagecopy($output, $logo1, 0, 0, 0, 0, imagesx($logo1) - 1, imagesy($logo1) - 1);
imagecopy($output, $logo2, imagesx($logo2), 0, 0, 0, imagesx($logo2) - 1, imagesy($logo2) - 1);
imagedestroy($logo1);
imagedestroy($logo2);

// Вывод различий
header('Content-Type: image/png');
imagepng($output);
imagedestroy($output);
?>


