refentry.refsect1:
&reftitle.description;
public arrayImagickKernel::getMatrix
Получить 2d матрицу значений, используемых в этом ядре. Элементы являются либо числом с плавающей запятой для используемых элементов, либо 'false', если элемент должен быть пропущен.

  &reftitle.examples;



       ImagickKernel::getMatrix


<?php


function renderKernelTable($matrix) {
    $output = "<table class='infoTable'>";

    foreach ($matrix as $row) {
        $output .= "<tr>";
        foreach ($row as $cell) {
            $output .= "<td style='text-align:left'>";
            if ($cell === false) {
                $output .= "false";
            }
            else {
                $output .= round($cell, 3);
            }
            $output .= "</td>";
        }
        $output .= "</tr>";
    }

    $output .= "</table>";

    return $output;
}

    $output = "Имя встроенного ядра 'ring' с параметрами '2,3.5':<br/>";
    $kernel = \ImagickKernel::fromBuiltIn(
        \Imagick::KERNEL_RING,
        "2,3.5"
    );
    $matrix = $kernel->getMatrix();
    $output .= renderKernelTable($matrix);

    echo $output;

?>
