refentry.refsect1:
&reftitle.description;

 boolimap_append
 resourceimap_stream
 stringmailbox
 stringmessage
 stringoptions&null;
 stringinternal_date&null;



 Добавляет строку message в указанный mailbox.

  &reftitle.examples;



    Пример использования imap_append 


<?php
$stream = imap_open("{imap.example.org}INBOX.Drafts", "username", "password");

$check = imap_check($stream);
echo "Кол-во сообщений до добавления: ". $check->Nmsgs . "\n";

imap_append($stream, "{imap.example.org}INBOX.Drafts"
                   , "From: me@example.com\r\n"
                   . "To: you@example.com\r\n"
                   . "Subject: test\r\n"
                   . "\r\n"
                   . "это проверочное сообщение, пожалуйста, игнорируйте его\r\n"
                   );

$check = imap_check($stream);
echo "Кол-во сообщений после добавления : ". $check->Nmsgs . "\n";

imap_close($stream);
?>
