refentry.refsect1:
&reftitle.description;
stdClassimap_mailboxmsginfo resourceimap
Проверяет статус текущего почтового ящика на сервере. Аналогично imap_status, но дополнительно вычисляет суммарный размер всех писем в ящике, из-за чего работает несколько медленнее.

  &reftitle.examples;



    Пример использования imap_mailboxmsginfo


<?php

$mbox = imap_open("{imap.example.org}INBOX", "username", "password")
      or die("не удалось подключиться: " . imap_last_error());

$check = imap_mailboxmsginfo($mbox);

if ($check) {
    echo "Date: "     . $check->Date    . "<br />\n" ;
    echo "Driver: "   . $check->Driver  . "<br />\n" ;
    echo "Mailbox: "  . $check->Mailbox . "<br />\n" ;
    echo "Messages: " . $check->Nmsgs   . "<br />\n" ;
    echo "Recent: "   . $check->Recent  . "<br />\n" ;
    echo "Unread: "   . $check->Unread  . "<br />\n" ;
    echo "Deleted: "  . $check->Deleted . "<br />\n" ;
    echo "Size: "     . $check->Size    . "<br />\n" ;
} else {
    echo "Вызов imap_mailboxmsginfo() завершился с ошибкой: " . imap_last_error() . "<br />\n";
}

imap_close($mbox);

?>
