    –ü—Ä–∏–º–µ—Ä —Ä–∞–±–æ—Ç—ã –º–µ—Ç–æ–¥–∞ Collator::getSortKey —Å —Ñ—É–Ω–∫—Ü–∏–µ–π usort


<?php

$data = [
    [ 'name' => 'üá≥üá± Derick Rethans', 'linked_account' => 'https://phpc.social/users/derickr' ],
    [ 'name' => 'Elephpant', 'linked_account' => 'https://phpc.social/phpc' ],
    [ 'name' => 'üá´üá∑ Marcus Bointon', 'linked_account' => 'https://phpc.social/users/Synchro' ],
];

/* –°–æ–∑–¥–∞—ë–º –æ–±—ä–µ–∫—Ç Collator */
$col = new Collator('en');

/* –û–ø—Ä–µ–¥–µ–ª—è–µ–º –ø–æ—Ä—è–¥–æ–∫ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏: –∑–∞–≥–ª–∞–≤–Ω—ã–µ –≤–∞—Ä–∏–∞–Ω—Ç—ã —Å–∏–º–≤–æ–ª–æ–≤ –∏–¥—É—Ç –ø–µ—Ä–µ–¥ —Å—Ç—Ä–æ—á–Ω—ã–º–∏. */
$col->setAttribute(Collator::CASE_FIRST, Collator::UPPER_FIRST);

/* –°–æ—Ä—Ç–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–æ–π —Ñ—É–Ω–∫—Ü–∏–µ–π, –∫–æ—Ç–æ—Ä–∞—è —Ç–∞–∫–∂–µ —É–¥–∞–ª—è–µ—Ç —ç–º–æ–¥–∑–∏ */
usort(
    $data,
    function($a, $b) use ($col) {
        /* –£–∫–∞–∑—ã–≤–∞–µ–º –∏–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä 'S', –∫–æ—Ç–æ—Ä—ã–π —É–¥–∞–ª–∏—Ç —Å–∏–º–≤–æ–ª—ã –Æ–Ω–∏–∫–æ–¥-–∫–ª–∞—Å—Å–∞ Symbols, –∏ —É–¥–∞–ª—è–µ–º –ø—Ä–æ–±–µ–ª—å–Ω—ã–µ —Å–∏–º–≤–æ–ª—ã
         * –≤ –Ω–∞—á–∞–ª–µ –∏ –∫–æ–Ω—Ü–µ —Å—Ç—Ä–æ–∫–∏ —Ñ—É–Ω–∫—Ü–∏–µ–π trim */
        $aName = trim(preg_replace('/\p{S}+/u', '', $a['name']));
        $bName = trim(preg_replace('/\p{S}+/u', '', $b['name']));

        /* –°–æ–∑–¥–∞—ë–º –∫–ª—é—á–∏ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏ */
        $aKey = $col->getSortKey($aName);
        $bKey = $col->getSortKey($bName);

        /* –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º –∫–ª—é—á–∏, —á—Ç–æ–±—ã –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å –ø–æ—Ä—è–¥–æ–∫ —ç–ª–µ–º–µ–Ω—Ç–æ–≤. */
        return $aKey <=> $bKey;
    }
);

var_dump($data);

?>


