refentry.refsect1:
&reftitle.returnvalues;


 Функция возвращает &true;, если именованная константа, заданная в параметре
 constant_name, была определена, иначе &false;.

  &reftitle.examples;



    Проверка констант


<?php

/* Обратите внимание на кавычки, это важно. Пример проверяет,
 * имя ли константы TEST строка «TEST». */
if (defined('TEST')) {
    echo TEST;
}

interface bar {
    const test = 'foobar!';
}

class foo {
    const test = 'foobar!';
}

var_dump(defined('bar::test')); // bool(true)
var_dump(defined('foo::test')); // bool(true)

?>




    Проверка вариантов перечисления (начиная с PHP 8.1.0)


<?php

enum Suit
{
    case Hearts;
    case Diamonds;
    case Clubs;
    case Spades;
}

var_dump(defined('Suit::Hearts')); // bool(true)

?>
