   Пример использования MongoDB\Driver\Command::__construct


    Команды также могут принимать опции, как часть нормальной структуры, которую вы 
    создаете и отправляете на сервер. К примеру, с большинством команд можно 
    передавать опцию maxTimeMS для ограничения времени их выполнения.



<?php

$manager = new MongoDB\Driver\Manager("mongodb://localhost:27017");
$command = new MongoDB\Driver\Command(
    array(
        "distinct" => "beer",
        "key" => "beer_name",
        "maxTimeMS" => 10,
    )
);

try {
    $cursor = $manager->executeCommand("beerdb", $command);
    $response = $cursor->toArray()[0];
} catch(MongoDB\Driver\Exception\Exception $e) {
    echo $e->getMessage(), "\n";
    exit;
}
var_dump($response);

?>


   &example.outputs.similar;

