refentry.refsect1:
&reftitle.returnvalues;


 Возвращает &true;, если еще есть результаты, которые не были отправлены
 клиенту, или &false; в противном случае.

  &reftitle.examples;

   Пример выполнения MongoDB\Driver\Cursor::isDead


<?php

/* В этом примере мы добавляем несколько результаты в коллекцию и указываем
 * меньший batchSize, чтобы гарантировать, что первая порция содержит только подмножество
 * результатов и курсор является открытым на сервере. */
$manager = new MongoDB\Driver\Manager("mongodb://localhost:27017");
$query = new MongoDB\Driver\Query([], ['batchSize' => 2]);

$bulk = new MongoDB\Driver\BulkWrite;
$bulk->insert(['x' => 1]);
$bulk->insert(['x' => 2]);
$bulk->insert(['x' => 3]);
$manager->executeBulkWrite('db.collection', $bulk);

$cursor = $manager->executeQuery('db.collection', $query);

var_dump($cursor->isDead());
$cursor->toArray();
var_dump($cursor->isDead());

?>


   &example.outputs;


bool(false)
bool(true)
