refentry.refsect1:
&reftitle.description;
intmssql_num_fields resourceresult
mssql_num_fields возвращает количество столбцов в результирующем наборе.

&reftitle.parameters;
result
Результирующий набор, полученный как результат вызова mssql_query.

  &reftitle.examples;



    Пример mssql_num_fields


<?php
// Соединяемся с MSSQL и выбираем базу
$link = mssql_connect('KALLESPC\SQLEXPRESS', 'sa', 'phpfi');
mssql_select_db('php', $link);

// Делаем запрос
$data = mssql_query('SELECT [name], [age] FROM [php].[dbo].[persons]');

// Создаем таблицу
echo '<table border="1">';

$header = false;

// Проходим по результирующему набору
while ($row = mssql_fetch_array($data)) {
    // Строим заголовок таблицы
    if (!$header) {
        echo '<thead>';
        echo '<tr>';

        for ($i = 1; ($i + 1) <= mssql_num_fields($data); ++$i) {
            echo '<td>' . ucfirst($row[$i]) . '</td>';
        }

        echo '</tr>';
        echo '</thead>';
        echo '<tbody>';

        $header = true;
    }

    // Строим строку
    echo '<tr>';

    foreach($row as $value) {
        echo '<td>' . $value . '</td>';
    }

    echo '</tr>';
}

// Закрываем таблицу
echo '</tbody>';
echo '</table>';

// CОчищаем результат
mssql_free_result($data);
mssql_close($link);
?>
