&reftitle.description;
resourcefalsemysql_connect stringserverini_get("mysql.default_host") stringusernameini_get("mysql.default_user") stringpasswordini_get("mysql.default_password") boolnew_link&false; intclient_flags0
Открывает новое соединение с сервером MySQL или использует уже существующее.

&reftitle.returnvalues;


 Возвращает дескриптор соединения с MySQL в случае успешного
 выполнения&return.falseforfailure;.

  &reftitle.examples;



    Пример использования mysql_connect


<?php
$link = mysql_connect('localhost', 'mysql_user', 'mysql_password');
if (!$link) {
    die('Ошибка соединения: ' . mysql_error());
}
echo 'Успешно соединились';
mysql_close($link);
?>







    Пример использования mysql_connect с синтаксисом hostname:port


<?php
// соединяемся с example.com на порту 3307
$link = mysql_connect('example.com:3307', 'mysql_user', 'mysql_password');
if (!$link) {
    die('Ошибка соединения: ' . mysql_error());
}
echo 'Успешно соединились';
mysql_close($link);

// соединяемся с localhost на порту 3307
$link = mysql_connect('127.0.0.1:3307', 'mysql_user', 'mysql_password');
if (!$link) {
    die('Ошибка соединения: ' . mysql_error());
}
echo 'Успешно соединились';
mysql_close($link);
?>







    Пример использования mysql_connect с синтаксисом ":/path/to/socket"


<?php
// соединяемся к localhost по сокету, т.е. /tmp/mysql.sock

// вариант 1: не указываем localhost
$link = mysql_connect(':/tmp/mysql', 'mysql_user', 'mysql_password');
if (!$link) {
    die('Ошибка соединения: ' . mysql_error());
}
echo 'Успешно соединились';
mysql_close($link);


// вариант 2: указываем localhost
$link = mysql_connect('localhost:/tmp/mysql.sock', 'mysql_user', 'mysql_password');
if (!$link) {
    die('Ошибка соединения: ' . mysql_error());
}
echo 'Успешно соединились';
mysql_close($link);
?>
