refentry.refsect1:
&reftitle.description;
stringfalsemysql_field_name resourceresult intfield_offset
mysql_field_name возвращает название колонки с указанным индексом.

  &reftitle.examples;

   Пример использования mysql_field_name


<?php
/* Таблица пользователей состоит из трёх колонок:
 *   user_id
 *   username
 *   password.
 */
$link = mysql_connect('localhost', 'mysql_user', 'mysql_password');
if (!$link) {
    die('Ошибка соединения с MySQL: ' . mysql_error());
}
$dbname = 'mydb';
$db_selected = mysql_select_db($dbname, $link);
if (!$db_selected) {
    die("Не удалось выбрать базу $dbname: " . mysql_error());
}
$res = mysql_query('select * from users', $link);

echo mysql_field_name($res, 0) . "\n";
echo mysql_field_name($res, 2);
?>


   &example.outputs;


user_id
password
