refentry.refsect1:
&reftitle.description;
&style.oop;
boolmysqli::commit intflags stringname
&style.procedural;
boolmysqli_commit mysqlilink intflags stringname
Фиксирует транзакцию для установленного соединения с базой данных.

&reftitle.parameters;



  &mysqli.link.description;

   flags



     Битмаска констант MYSQLI_TRANS_COR_*.




   name



     Если передан, то выполняется COMMIT/*name*/.

  &reftitle.examples;

   Пример использования mysqli::commit

&style.oop;


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Проверить соединение */
if (mysqli_connect_errno()) {
    printf("Connect failed: %s\n", mysqli_connect_error());
    exit();
}

$mysqli->query("CREATE TABLE Language LIKE CountryLanguage");

/* Откл. автофиксацию изменений */
$mysqli->autocommit(FALSE);

/* Вставить некоторые значения */
$mysqli->query("INSERT INTO Language VALUES ('DEU', 'Bavarian', 'F', 11.2)");
$mysqli->query("INSERT INTO Language VALUES ('DEU', 'Swabian', 'F', 9.4)");

/* Фиксировать транзакцию */
if (!$mysqli->commit()) {
    print("Не удалось зафиксировать транзакцию\n");
    exit();
}

/* Удалить таблицу */
$mysqli->query("DROP TABLE Language");

/* Закрыть соединение */
$mysqli->close();
?>



&style.procedural;


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "test");

/* Проверить соединение */
if (!$link) {
    printf("Connect failed: %s\n", mysqli_connect_error());
    exit();
}

/* Откл. автофиксацию изменений */
mysqli_autocommit($link, FALSE);

mysqli_query($link, "CREATE TABLE Language LIKE CountryLanguage");

/* Вставить некоторые значения */
mysqli_query($link, "INSERT INTO Language VALUES ('DEU', 'Bavarian', 'F', 11.2)");
mysqli_query($link, "INSERT INTO Language VALUES ('DEU', 'Swabian', 'F', 9.4)");

/* Фиксировать транзакцию */
if (!mysqli_commit($link)) {
    print("Не удалось зафиксировать транзакцию\n");
    exit();
}

/* Закрыть соединение */
mysqli_close($link);
?>
