refentry.refsect1:
        &reftitle.description;

&style.oop;

            boolmysqli_result::data_seek
            intoffset


&style.procedural;

            boolmysqli_data_seek
            mysqli_resultresult
            intoffset



            Функция mysqli_data_seek перемещает указатель в результате на строку,
            указанную в параметре offset.

&reftitle.parameters;



        &mysqli.result.description;

            offset



                    Смещение. Должно быть между нулем и числом строк в результате минус один
                    (0..mysqli_num_rows - 1).

&reftitle.notes;



        Эта функция может быть использована только с буферизованными результатами, которые можно получить
        использованием функций mysqli_store_result или mysqli_query.

        &reftitle.examples;

            &style.oop;


<?php
/* Создание подключения */
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Проверка подключения */ 
if (mysqli_connect_errno()) {
    printf("Connect failed: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, CountryCode FROM City ORDER BY Name";
if ($result = $mysqli->query( $query)) {

    /* Переход к строке №400 */
    $result->data_seek(399);

    /* Получение строки */
    $row = $result->fetch_row();

    printf ("City: %s  Countrycode: %s\n", $row[0], $row[1]);

    /* Закрытие набора данных */
    $result->close();
}

/* Закрытие соединения */
$mysqli->close();
?>




            &style.procedural;


<?php
/* Создание подключения */
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Проверка подключения */ 
if (!$link) {
    printf("Connect failed: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, CountryCode FROM City ORDER BY Name";

if ($result = mysqli_query($link, $query)) {

    /* Переход к строке №400 */
    mysqli_data_seek($result, 399);

    /* Получение строки */
    $row = mysqli_fetch_row($result);

    printf ("City: %s  Countrycode: %s\n", $row[0], $row[1]);

    /* Закрытие набора данных */
    mysqli_free_result($result);
}

/* Закрытие соединения */
mysqli_close($link);
?>


            &examples.outputs;


City: Benin City  Countrycode: NGA
