refentry.refsect1:
  &reftitle.description;

&style.oop;
  
   objectmysqli_result::fetch_field
   
  

&style.procedural;
  
   objectmysqli_fetch_field
   mysqli_resultresult
  


   Возвращает информацию об одном столбце результирующего набора в виде объекта.
   Чтобы получить определения всех столбцов, просто запустите эту функцию
   многократно.

  &reftitle.examples;
  
   &style.oop;
   

<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* проверка подключения */
if (mysqli_connect_errno()) {
    printf("Не удалось подключиться: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = $mysqli->query($query)) {

    /* Получим информацию обо всех столбцах */
    while ($finfo = $result->fetch_field()) {

        printf("Имя:         %s\n", $finfo->name);
        printf("Таблица:     %s\n", $finfo->table);
        printf("макс. длина: %d\n", $finfo->max_length);
        printf("Флаги:       %d\n", $finfo->flags);
        printf("Тип:         %d\n\n", $finfo->type);
    }
    $result->close();
}

/* закрываем подключение */
$mysqli->close();
?>

   
  
  
   &style.procedural;
   

<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* проверка подключения */
if (mysqli_connect_errno()) {
    printf("Не удалось подключиться: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Получим информацию обо всех столбцах */
    while ($finfo = mysqli_fetch_field($result)) {

        printf("Имя:         %s\n", $finfo->name);
        printf("Таблица:     %s\n", $finfo->table);
        printf("макс. длина: %d\n", $finfo->max_length);
        printf("Флаги:       %d\n", $finfo->flags);
        printf("Тип:         %d\n\n", $finfo->type);    }
    mysqli_free_result($result);
}

/* закрываем подключение */
mysqli_close($link);
?>

   
   &examples.outputs;
   

Имя:         Name
Таблица:     Country
макс. длина: 11
Флаги:       1
Тип:         254

Имя:         SurfaceArea
Таблица:     Country
макс. длина: 10
Флаги:       32769
