refentry.refsect1:
&reftitle.description;
public arrayOAuth::getAccessToken stringaccess_token_url stringauth_session_handle stringverifier_token stringhttp_method
Получает токен доступа, его пароль и все дополнительные параметры ответа от провайдера сервиса.

  &reftitle.examples;



    Пример использования OAuth::getAccessToken


<?php
try {
    $oauth = new OAuth(OAUTH_CONSUMER_KEY,OAUTH_CONSUMER_SECRET);
    $oauth->setToken($request_token,$request_token_secret);
    $access_token_info = $oauth->getAccessToken("https://example.com/oauth/access_token");
    if(!empty($access_token_info)) {
        print_r($access_token_info);
    } else {
        print "Failed fetching access token, response was: " . $oauth->getLastResponse();
    }
} catch(OAuthException $E) {
    echo "Response: ". $E->lastResponse . "\n";
}
?>


    &example.outputs.similar;


Array
(
    [oauth_token] => some_token
    [oauth_token_secret] => some_token_secret
)
