refentry.refsect1:
&reftitle.parameters;




   connection

    &oci.parameter.connection;



   module_name



     Задаваемая пользователем строка string длиной до 48 байт.

  &reftitle.examples;



    Установка имени модуля


<?php

$c = oci_connect('hr', 'welcome', 'localhost/XE');

// Запись модуля
oci_set_module_name($c, 'Home Page');

// Код, осуществляющий запрос к БД, например выборка:
$s = oci_parse($c, 'select * from dual');
oci_execute($s);
oci_fetch_all($s, $res);

sleep(30);
?>




// Пока скрипт выполняется, администратор может увидеть, какие модули
// используются:

sqlplus system/welcome
SQL> select module from v$session;
