refentry.refsect1:
&reftitle.description;
arraypg_meta_data resourceconnection stringtable_name boolextended&false;
pg_meta_data возвращает определение таблицы table_name в виде массива.

  &reftitle.examples;



    Получение метаданных таблицы


<?php
  $dbconn = pg_connect("dbname=publisher") or die("Не удалось соединиться с сервером");

  $meta = pg_meta_data($dbconn, 'authors');
  if (is_array($meta)) {
      echo '<pre>';
      var_dump($meta);
      echo '</pre>';
  }
?>


