&return.success;


   Пользуйтесь функцией pg_get_result для получения 
   результата запроса.

  &reftitle.examples;



    Пример использования pg_send_query_params


<?php
  $dbconn = pg_connect("dbname=publisher") or die("Could not connect");

  // Использование параметров. Стоит заметить, что нет необходимости 
  // заключать в кавычки и экранировать параметр.
  pg_send_query_params($dbconn, 'select count(*) from authors where city = $1', array('Perth'));

  // В сравнении с pg_send_query 
  $str = pg_escape_string('Perth');
  pg_send_query($dbconn, "select count(*) from authors where city = '${str}'");
?>
