&reftitle.description;
public voidpht\Thread::addClassTask stringclassName mixedctorArgs
Добавляет новую задачу класса во внутреннюю очередь задач pht\Thread.

  &reftitle.examples;



    Добавление задачи нового класса в поток


<?php

use pht\{Thread, Runnable};

class Task implements Runnable
{
    private $one;

    public function __construct(int $one)
    {
        $this->one = $one;
    }

    public function run()
    {
        var_dump($this->one);
    }
}

$thread = new Thread();

$thread->addClassTask(Task::class, 1);

$thread->start();
$thread->join();


    &example.outputs;


int(1)
