  &reftitle.examples;



    Обновление значения вектора


<?php

use pht\Vector;

$vector = new Vector();

$vector[] = 1;
$vector[] = 2;

$vector->updateAt(3, 0);
$vector[1] = 4;

var_dump($vector);


    &example.outputs;


object(pht\Vector)#1 (2) {
  [0]=>
  int(3)
  [1]=>
  int(4)
}
