refentry.refsect1:
&reftitle.description;
intradius_cvt_int stringdata
Преобразует необработанные данные в целое число

  &reftitle.examples;



    Пример использования radius_cvt_int


<?php
while ($resa = radius_get_attr($res)) {

    if (!is_array($resa)) {
        printf ("Ошибка при получении атрибута: %s\n",  radius_strerror($res));
        exit;
    }

    $attr = $resa['attr'];
    $data = $resa['data'];

    switch ($attr) {

    case RADIUS_FRAMED_MTU:
        $mtu = radius_cvt_int($data);
        echo "Максимальная единица передачи: $mtu<br>\n";
        break;
    }
}
?>
