 &reftitle.examples;

  –ü—Ä–∏–º–µ—Ä –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ —Å–ª—É—á–∞–π–Ω—ã—Ö –∑–Ω–∞—á–µ–Ω–∏–π –º–µ—Ç–æ–¥–∞–º–∏ –º–æ–¥—É–ª—è Random


<?php

$r = new \Random\Randomizer();

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Å–ª—É—á–∞–π–Ω–æ–≥–æ –¥–æ–º–µ–Ω–Ω–æ–≥–æ –∏–º–µ–Ω–∏
printf(
    "%s.example.com\n",
    $r->getBytesFromString('abcdefghijklmnopqrstuvwxyz0123456789', 16)
);

// –ü–µ—Ä–µ–º–µ—à–∏–≤–∞–Ω–∏–µ –º–∞—Å—Å–∏–≤–∞:
$fruits = ['red' => 'üçé', 'green' => 'ü•ù', 'yellow' => 'üçå', 'pink' => 'üçë', 'purple' => 'üçá'];
echo "–°–∞–ª–∞—Ç: ", implode(', ', $r->shuffleArray($fruits)), "\n";

// –í—ã–±–æ—Ä —Å–ª—É—á–∞–π–Ω—ã—Ö –∫–ª—é—á–µ–π –º–∞—Å—Å–∏–≤–∞
$fruits = ['red' => 'üçé', 'green' => 'ü•ù', 'yellow' => 'üçå', 'pink' => 'üçë', 'purple' => 'üçá'];

$keys = $r->pickArrayKeys($fruits, 2);
// –ü–æ–∏—Å–∫ –∑–Ω–∞—á–µ–Ω–∏–π –≤—ã–±—Ä–∞–Ω–Ω—ã—Ö –∫–ª—é—á–µ–π
$selection = array_map(
    static fn($key) => $fruits[$key],
    $keys
);

echo "–ó–Ω–∞—á–µ–Ω–∏—è: ", implode(', ', $selection), "\n";

?>


