refentry.refsect1:
  &reftitle.examples;



    Пример использования интерфейса обратного вызова readline


<?php
function rl_callback($ret)
{
    global $c, $prompting;

    echo "You entered: $ret\n";
    $c++;

    if ($c > 10) {
        $prompting = false;
        readline_callback_handler_remove();
    } else {
        readline_callback_handler_install("[$c] Поговори со мной: ", 'rl_callback');
    }
}

$c = 1;
$prompting = true;

readline_callback_handler_install("[$c] Enter something: ", 'rl_callback');

while ($prompting) {
    $w = NULL;
    $e = NULL;
    $n = stream_select($r = array(STDIN), $w, $e, null);
    if ($n && in_array(STDIN, $r)) {
        // читаем символ и вызываем функцию обратного вызова, 
        // если введен символ новой строки
        readline_callback_read_char();
    }
}

echo "Ввод отключен. Спасибо за внимание.\n";
?>
