refentry.refsect1:
&reftitle.description;

 arrayclass_implements
 mixedclass
 boolautoload&true;



 Функция возвращает массив имён интерфейсов, реализованных в заданном классе
 class и его родительских классах.

  &reftitle.examples;



    Пример использования class_implements


<?php

interface foo { }
class bar implements foo {}

print_r(class_implements(new bar));

// можно передавать имя класса вместо объекта
print_r(class_implements('bar'));


function __autoload($class_name) {
   require_once $class_name . '.php';
}

// использование __autoload для загрузки ещё незагруженного класса 'not_loaded'
print_r(class_implements('not_loaded', true));

?>


    &example.outputs.similar;


Array
(
    [foo] => foo
)

Array
(
    [interface_of_not_loaded] => interface_of_not_loaded
)
