refentry.refsect1:
&reftitle.description;

 arrayclass_parents
 mixedclass
 boolautoload&true;



 Эта функция возвращает массив с именами родительских классов заданного класса
 class.

  &reftitle.examples;



    Пример использования class_parents


<?php

class foo { }
class bar extends foo {}

print_r(class_parents(new bar));

// можно передавать имя класса вместо объекта
print_r(class_parents('bar'));


function __autoload($class_name) {
   require_once $class_name . '.php';
}

// использование __autoload для загрузки ещё незагруженного класса 'not_loaded'
print_r(class_parents('not_loaded', true));
?>


    &example.outputs.similar;


Array
(
    [foo] => foo
)

Array
(
    [parent_of_not_loaded] => parent_of_not_loaded
