refentry.refsect1:
&reftitle.description;
inttidy_access_count tidytidy
tidy_access_count возвращает число доступных предупреждений, найденных в рассмотренном документе.

  &reftitle.examples;



    Пример использования tidy_access_count


<?php

$html ='<!DOCTYPE html PUBLIC "-//W3C//DTD HTML 3.2//EN">
<html><head><title>Title</title></head>
<body>

<p><img src="img.png"></p>

</body></html>';


// выбирается уровень проверки доступности: 1, 2 или 3
$config = array('accessibility-check' => 3);

$tidy = new tidy();
$tidy->parseString($html, $config);
$tidy->cleanRepair();

/* Нельзя забывать про этот вызов! */
$tidy->diagnose();

echo tidy_access_count($tidy); //5

?>
