refentry.refsect1:
&reftitle.description;
bool ZipArchive::GetExternalAttributesIndex intindex intopsys intattr intflags
Извлекает внешние атрибуты записи по ее индексу.

  &reftitle.examples;


   В этом примере извлекаются все файлы из архива
   test.zip и им выставляюься
   права Unix, полученные из внешних атрибутов.


   Извлекаем все записи с их правами Unix


<?php
$zip = new ZipArchive();
if ($zip->open('test.zip') === TRUE) {
    for ($idx=0 ; $s = $zip->statIndex($idx) ; $idx++) {
        if ($zip->extractTo('.', $s['name'])) {
            if ($zip->getExternalAttributesIndex($idx, $opsys, $attr) 
                && $opsys==ZipArchive::OPSYS_UNIX) {
               chmod($s['name'], ($attr >> 16) & 0777);
            }
        }
    }
    $zip->close();
    echo "Ok\n";
} else {
    echo "KO\n";
}
?>
