refentry.refsect1:
&reftitle.description;
public intfalseZipArchive::locateName stringname intflags0
Находит элемент по имени.

  &reftitle.examples;


      Создать архив, а затем вызвать метод ZipArchive::locateName



<?php

$file = 'testlocate.zip';

$zip = new ZipArchive;
if ($zip->open($file, ZipArchive::CREATE) !== TRUE) {
    exit('Ошибка');
}

$zip->addFromString('entry1.txt', 'entry #1');
$zip->addFromString('entry2.txt', 'entry #2');
$zip->addFromString('dir/entry2d.txt', 'entry #2');

if ($zip->status !== ZipArchive::ER_OK) {
    echo "Ошибка записи в zip-файл\n";
}
$zip->close();

if ($zip->open($file) !== TRUE) {
    exit('Ошибка');
}

echo $zip->locateName('entry1.txt') . "\n";
echo $zip->locateName('eNtry2.txt') . "\n";
echo $zip->locateName('eNtry2.txt', ZipArchive::FL_NOCASE) . "\n";
echo $zip->locateName('enTRy2d.txt', ZipArchive::FL_NOCASE|ZipArchive::FL_NODIR) . "\n";
$zip->close();

?>



