refentry.refsect1:
  &reftitle.examples;

   Открытие и извлечение


<?php
$zip = new ZipArchive;
$res = $zip->open('test.zip');
if ($res === TRUE) {
    echo 'готово';
    $zip->extractTo('test');
    $zip->close();
} else {
    echo 'ошибка с кодом:' . $res;
}
?>




   Создание архива


<?php
$zip = new ZipArchive;
$res = $zip->open('test.zip', ZipArchive::CREATE);
if ($res === TRUE) {
    $zip->addFromString('test.txt', 'содержимое файла');
    $zip->addFile('data.txt', 'entryname.txt');
    $zip->close();
    echo 'готово';
} else {
    echo 'ошибка';
}
?>




     Создать временный архив


<?php
$name = tempnam(sys_get_temp_dir(), "FOO");
$zip = new ZipArchive;
$res = $zip->open($name, ZipArchive::OVERWRITE); /* усечение, поскольку пустой файл недопустим */
if ($res === TRUE) {
    $zip->addFile('data.txt', 'entryname.txt');
    $zip->close();
    echo 'готово';
} else {
    echo 'ошибка';
}
?>
