refentry.refsect1:
  &reftitle.examples;

   Открыть и извлечь


<?php

$zip = new ZipArchive();
$res = $zip->open('test.zip');

if ($res === true) {
    echo 'Готово';
    $zip->extractTo('test');
    $zip->close();
} else {
    echo 'Ошибка, код:' . $res;
}

?>




   Создать архив


<?php

$zip = new ZipArchive();
$res = $zip->open('test.zip', ZipArchive::CREATE);

if ($res === true) {
    $zip->addFromString('test.txt', 'содержимое файла');
    $zip->addFile('data.txt', 'entryname.txt');
    $zip->close();
    echo 'Готово';
} else {
    echo 'Ошибка';
}

?>




     Создать временный архив


<?php

$name = tempnam(sys_get_temp_dir(), "FOO");
$zip = new ZipArchive();
$res = $zip->open($name, ZipArchive::OVERWRITE); /* усечение, поскольку пустой файл недопустим */

if ($res === true) {
    $zip->addFile('data.txt', 'entryname.txt');
    $zip->close();
    echo 'готово';
} else {
    echo 'Ошибка';
}
?>
